import {Component} from 'react'

import {BsArrowLeft, BsArrowRight} from 'react-icons/bs'

import Cookies from 'js-cookie'

import Header from '../Header'

import './index.css'

class Popular extends Component {
  state = {
    pageNumber: 1,
  }

  componentDidMount() {
    this.getPopularMovies()
  }

  getPopularMovies = async () => {
    const {pageNumber} = this.state

    const apiKey = Cookies.get('request_token')

    const url = `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=en-US&page=${pageNumber}`

    const response = await fetch(url)
    const data = await response.json()
    console.log(data)
  }

  pageDecrease = () => {
    const {pageNumber} = this.state

    if (pageNumber > 1) {
      this.setState(prevState => ({
        pageNumber: prevState.pageNumber - 1,
      }))
    }
    this.getPopularMovies()
  }

  pageIncrease = () => {
    const {pageNumber} = this.state

    if (pageNumber < 20) {
      this.setState(prevState => ({
        pageNumber: prevState.pageNumber + 1,
      }))
    }
  }

  render() {
    const {pageNumber} = this.state

    return (
      <>
        <div className="popular-page-background">
          <Header />
          <div>
            <BsArrowLeft onClick={this.pageDecrease} />
            <p>{pageNumber} of 20</p>
            <BsArrowRight onClick={this.pageIncrease} />
          </div>
        </div>
      </>
    )
  }
}

export default Popular
