import {Component} from 'react'
import Cookies from 'js-cookie'

import Header from '../Header'

// import RandomOriginals from '../RandomOriginals'

import TrendingMovies from '../TrendingMovies'

import './index.css'
import TopRated from '../TopRated'
import Footer from '../Footer'

class Home extends Component {
  state = {randomOriginalData: {}}

  componentDidMount() {
    this.getOriginalsData()
    // this.renderRandomOriginalMovie()
  }

  getOriginalsData = async () => {
    const apiKey = Cookies.get('request_token')

    const url = `https://api.themoviedb.org/3/discover/tv?api_key=${apiKey}`

    const options = {
      method: 'GET',
    }

    const response = await fetch(url, options)
    const data = await response.json()
    const resultsArr = data.results

    const random = Math.floor(Math.random() * resultsArr.length)

    const updatedOriginalObj = {
      backdropPath: resultsArr[random].backdrop_path,
      id: resultsArr[random].id,
      name: resultsArr[random].name,
      overview: resultsArr[random].overview,
    }

    this.setState({randomOriginalData: updatedOriginalObj})

    // console.log(updatedOriginalObj)
  }

  //   renderRandomOriginalMovie = async () => {
  //     const {randomOriginalData} = this.state

  //     const {backdropPath} = randomOriginalData

  //     const url = `https://image.tmdb.org/t/p/original/${backdropPath}`

  //     const options = {
  //       method: 'GET',
  //     }

  //     // const response = await fetch(url, options)

  //     // console.log(backdropPath)
  //   }

  render() {
    const {randomOriginalData} = this.state

    const {backdropPath, name, overview} = randomOriginalData

    // console.log(randomOriginalData)

    // const re =
    //   backdropPath === undefined
    //     ? null
    //     : `https://image.tmdb.org/t/p/original${backdropPath}`

    return (
      <>
        <div
          className="original-image"
          style={{
            backgroundImage: `url(
          https://image.tmdb.org/t/p/original${backdropPath}
        )`,
          }}
        >
          <div>
            <Header />
            <p className="movie-name">{name}</p>
            <p className="overview-text">{overview}</p>
            <button className="play-btn" type="button">
              Play
            </button>
          </div>
          <div className="empty-container">
            <p> </p>
          </div>
        </div>
        <div className="all-category-movies">
          <TrendingMovies />
          <TopRated />
          <Footer />
        </div>
      </>
    )
  }
}

export default Home
