import {Component} from 'react'

import Slider from 'react-slick'

import Cookies from 'js-cookie'

import './index.css'

const settings = {
  dots: false,
  infinite: false,
  speed: 500,
  slidesToShow: 4,
  slidesToScroll: 1,
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 4,
        slidesToScroll: 3,
        infinite: true,
      },
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2,
        initialSlide: 2,
      },
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 3,
        slidesToScroll: 3,
      },
    },
  ],
}

class TrendingMovies extends Component {
  state = {
    moviesList: [],
  }

  componentDidMount() {
    this.getMovies()
  }

  getMovies = async () => {
    const apiKey = Cookies.get('request_token')

    const url = `https://api.themoviedb.org/3/trending/all/week?api_key=${apiKey}`

    const options = {
      method: 'GET',
    }

    const response = await fetch(url, options)

    const data = await response.json()

    const updatedTrendingMovies = data.results.map(eachMovie => ({
      posterPath: eachMovie.poster_path,
      id: eachMovie.id,
    }))

    this.setState({moviesList: updatedTrendingMovies})

    // console.log(data)
  }

  renderSlider = () => {
    const {moviesList} = this.state

    return (
      <Slider {...settings}>
        {/* {moviesList.map(movie => {
          const movieImage = `https://image.tmdb.org/t/p/original/${movie.posterPath}`
          return (
            <div className="react-slick-item" key={movie.id}>
              <img
                alt="ab"
                className="poster"
                src={movieImage}
                width="100%"
                height="100%"
              />
            </div>
          )
        })} */}

        {moviesList.map(movie => (
          <div>
            <img
              src={`https://image.tmdb.org/t/p/original/${movie.posterPath}`}
              alt={movie.id}
            />
          </div>
        ))}
      </Slider>
    )
  }

  render() {
    const {moviesList} = this.state

    // console.log(moviesList)

    return (
      <div>
        <h3 className="trending-heading">Trending Now</h3>
        <div className="slick-app-container">
          {/* <h1>Netflix Originals</h1> */}
          <div className="trending-list">
            {moviesList.length ? (
              this.renderSlider()
            ) : (
              <p style={{textAlign: 'center'}}>Loading...................</p>
            )}
          </div>
        </div>
      </div>
    )
  }
}

export default TrendingMovies
