import {Component} from 'react'

import Cookies from 'js-cookie'

import {Link} from 'react-router-dom'

import {BsSearch} from 'react-icons/bs'

import './index.css'

class Search extends Component {
  state = {searchList: []}

  componentDidMount() {
    this.getSearchInput()
  }

  getSearchInput = async () => {
    const {inputSearch} = this.state

    console.log(inputSearch)

    // const apiKey = Cookies.get('request_token')

    // const url = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&query=${inputSearch}&page={1}`

    // const options = {
    //   method: 'GET',
    // }

    // const response = await fetch(url, options)
    // const data = await response.json()

    // console.log(data)
  }

  changeInput = async event => {
    console.log(event.target.value)

    const apiKey = Cookies.get('request_token')

    const url = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&query=${event.target.value}&page={1}`

    const options = {
      method: 'GET',
    }

    const response =
      event.target.value !== '' ? await fetch(url, options) : null

    const data = await response.json()
    const updatedSearchList = data.results.map(eachMovie => ({
      id: eachMovie.id,
      posterPath: eachMovie.poster_path,
      title: eachMovie.title,
    }))

    console.log(updatedSearchList)

    this.setState({searchList: updatedSearchList})

    // history.replace(`/search/${event.target.value}`)
  }

  render() {
    const {updatedSearchList} = this.state

    return (
      <div className="movie-search-container ">
        <nav className="search-movies-navbar">
          <ul className="sides-navbar left-side">
            <li>
              <img
                className="header-movies-logo"
                src="https://res.cloudinary.com/dtxgq5uxo/image/upload/v1630941228/Group_7399_gvhjyc.png"
                alt="logo"
              />
            </li>
            <Link className="nav-link" to="/">
              <li>Home</li>
            </Link>
            <Link className="nav-link" to="/popular">
              <li>Popular</li>
            </Link>
          </ul>
          <ul className="sides-navbar right-side">
            <li>
              <div className="search-container">
                <input
                  type="search"
                  className="input-element"
                  placeholder="Search"
                  onChange={this.changeInput}
                />
                <BsSearch className="search-icon" />
              </div>
            </li>
            <li>
              <Link to="/account">
                <img
                  src="https://res.cloudinary.com/dtxgq5uxo/image/upload/v1630982240/149071_evz1bo.png"
                  alt="profile logo"
                  className="account-logo"
                />
              </Link>
            </li>
          </ul>
        </nav>
        <div>
          {updatedSearchList.length === 0 ? (
            <h1 className="search-text">Search Movies</h1>
          ) : null}
        </div>
      </div>
    )
  }
}

export default Search
