import {Component} from 'react'

import {BsPlayFill} from 'react-icons/bs'

import Cookies from 'js-cookie'
import Header from '../Header'

class MovieDetails extends Component {
  state = {
    movieDetails: {},
  }

  componentDidMount() {
    this.getMovieDetails()
  }

  getMovieDetails = async () => {
    const {match} = this.props
    const {params} = match
    const {id} = params

    const apiKey = Cookies.get('request_token')
    const url = `https://api.themoviedb.org/3/movie/${id}?api_key=${apiKey}&language=en-US`

    const options = {
      method: 'GET',
    }

    const response = await fetch(url, options)
    const data = await response.json()
    const updatedMovieDetails = {
      adult: data.adult,
      backdropPath: data.backdrop_path,
      budget: data.budget,
      genres: data.genres,
      id: data.id,
      originalTitle: data.original_title,
      overview: data.overview,
      releaseDate: data.release_date,
      runtime: data.runtime,
      voteAverage: data.vote_average,
      voteCount: data.vote_count,
    }

    this.setState({movieDetails: updatedMovieDetails})
    console.log(data)
    console.log(updatedMovieDetails)
  }

  render() {
    const {movieDetails} = this.state

    const {
      adult,
      backdropPath,
      budget,
      genres,
      id,
      originalTitle,
      overview,
      releaseDate,
      runtime,
      voteAverage,
      voteCount,
    } = movieDetails

    return (
      <div
        className="original-image"
        style={{
          backgroundImage: `url(
          https://image.tmdb.org/t/p/original${backdropPath}
        )`,
        }}
      >
        <div>
          <Header />
          <p className="movie-name">{originalTitle}</p>
          <div>
            <p>{runtime} min</p>
            {adult ? <p>U</p> : <p>UA</p>}
            <p>{releaseDate.split('-')}</p>
          </div>
          <p className="overview-text">{overview}</p>
          <div className="play-btn">
            <BsPlayFill />
            <button className="play" type="button">
              Play
            </button>
          </div>
        </div>
        <div className="empty-container">
          <p> </p>
        </div>
      </div>
    )
  }
}

export default MovieDetails
