import {Component} from 'react'
import Header from '../Header'

class Home extends Component {
  state = {
    originalsData: [],
  }

  componentDidMount() {
    // this.getTrendingMovies()
    this.getOriginals()
  }

  getTrendingMovies = async () => {
    const url =
      'https://api.themoviedb.org/3/trending/all/week?api_key=970d698cc21f18d0759bec348ed6e217'

    const options = {
      method: 'GET',
    }

    const response = await fetch(url, options)
    const data = await response.json()

    console.log(data)
  }

  getOriginals = async () => {
    const url =
      'https://api.themoviedb.org/3/discover/tv?api_key=970d698cc21f18d0759bec348ed6e217'

    const options = {
      method: 'GET',
    }

    const response = await fetch(url, options)
    const data = await response.json()

    const updatedOriginalData = data.results.map(eachOriginal => ({
      backdropPath: eachOriginal.backdrop_path,
      id: eachOriginal.id,
      name: eachOriginal.name,
      overview: eachOriginal.overview,
    }))

    console.log(updatedOriginalData)

    this.setState(
      {originalsData: updatedOriginalData},
      this.renderRandomOriginals,
    )
  }

  renderRandomOriginals = () => {
    const {originalsData} = this.state

    console.log(originalsData)

    const randomOriginal =
      originalsData[Math.floor(Math.random() * originalsData.length)]

    // const {backdropPath, name, overview} = randomOriginal

    // console.log(randomOriginal)

    return (
      <div>
        <Header />
        <button type="button">Play</button>
        {/* <h1>{randomOriginal.name}</h1> */}
      </div>
    )
  }

  render() {
    return <>{this.renderRandomOriginals()}</>
  }
}

export default Home
